mapper.select:
SELECT tc.id,tc.fullname,tc.appellation,tc.owner,tc.company,tc.job,tc.email,tc.phone,
       tc.website,tc.mphone,tc.state,tc.source,tu.name AS create_by,tc.create_time,
       tu2.name AS edit_by,tc.edit_time,tc.description,tc.contact_summary,tc.next_contact_time,tc.address
FROM tbl_clue tc
JOIN tbl_user tu ON tu.id = tc.create_by
LEFT JOIN tbl_user tu2 ON tu2.id = tc.edit_by


    AND tc.fullname = #{fullname}


    AND tc.company = #{company}


    AND tc.phone = #{phone}


    AND tc.source = #{source}


    AND tc.owner = #{owner}


    AND tc.mphone = #{mphone}


    AND tc.state = #{state}


ORDER BY tc.create_time DESC

SELECT COUNT(*)
FROM tbl_clue tc
JOIN tbl_user tu ON tu.id = tc.create_by
LEFT JOIN tbl_user tu2 ON tu2.id = tc.edit_by


    AND tc.fullname = #{fullname}


    AND tc.company = #{company}


    AND tc.phone = #{phone}


    AND tc.source = #{source}


    AND tc.owner = #{owner}


    AND tc.mphone = #{mphone}


    AND tc.state = #{state}
