mapper.select:
SELECT a.id,a.owner,a.name,a.start_date,a.end_date,a.cost,a.description,a.create_time, tu.name as create_by,tu2.name as edit_by,a.edit_time FROM tbl_activity a JOIN tbl_user tu on a.create_by = tu.id LEFT JOIN tbl_user tu2 on a.edit_by = tu2.id
AND a.name LIKE '%' #{name} '%'
AND a.owner LIKE '%' #{owner} '%'
AND a.start_date >= #{startDate}
AND a.end_date <= #{endDate}
ORDER BY a.create_time DESC

SELECT COUNT(*) FROM tbl_activity a JOIN tbl_user tu on a.create_by = tu.id LEFT JOIN tbl_user tu2 on a.edit_by = tu2.id
AND a.name LIKE '%' #{name} '%'
AND a.owner LIKE '%' #{owner} '%'
AND a.start_date >= #{startDate}
AND a.end_date <= #{endDate}
